mapper.update:
UPDATE A_qna_comment
SET deleted='yes'
WHERE num=#{num}

UPDATE A_qna_comment
SET content=#{content}
WHERE num=#{num}

mapper.select:
SELECT num, writer, content, ref_group, comment_group, deleted, regdate
FROM A_qna_comment
WHERE num=#{num}

SELECT NVL(MAX(ROWNUM), 0)
FROM A_qna_comment
WHERE ref_group=#{ref_group}

mapper.update:
UPDATE A_qna
SET comm=comm+1
WHERE num=#{num}

UPDATE A_qna
SET comm=comm-1
WHERE num=#{num}

UPDATE A_qna
SET done=1
WHERE num=#{num}
