mapper.select:
SELECT result1.* FROM (SELECT num, writer, title, comm, done, content, hit, TO_CHAR(regdate, 'YYYY.MM.DD HH24:MI') AS regdate, LAG(num, 1, 0) OVER (ORDER BY num DESC) AS prevNum, LEAD(num, 1, 0) OVER (ORDER BY num DESC) AS nextNum FROM A_qna
WHERE title LIKE '%'||#{title}||'%' OR content LIKE '%'||#{content}||'%'
WHERE title LIKE '%'||#{title}||'%'
WHERE writer LIKE '%'||#{writer}||'%'
) result1 WHERE num=#{num}

mapper.update:
UPDATE A_qna
SET title=#{title}, content=#{content}
WHERE num=#{num}

mapper.select:
select nvl(max(rownum), 0)
from A_qna 
where writer = #{writer}

mapper.update:
UPDATE A_qna
SET hit=hit+1
WHERE num=#{num}
