mapper.select:
select price
from A_room_price
where weight=#{weight} and room_name=#{room_name}

select num
from A_room
where room_name=#{room_name} and weight=#{weight}

mapper.update:
update A_room
set room_reserve_state='yes'
where num=#{room_num}

mapper.select:
select num, room_name, weight, room_reserve_state
from A_room
where num=#{num}
