mapper.select:
select Count(*)
from A_reserve
where member_id=#{member_id}

select price
from A_room_price
where weight=#{weight} and room_name=#{room_name}

select num
from A_room
where room_name=#{room_name} and weight=#{weight}

mapper.update:
update A_room
set room_reserve_state='yes'
where num=#{room_num}

mapper.select:
select num, room_name, weight, room_reserve_state
from A_room
where num=#{num}

mapper.update:
update A_reserve
set state='예약취소'
where num=#{num}

update A_room
set room_reserve_state='no'
where num=#{room_num}

update A_reserve
set state='예약확인'
where num=#{num}

update A_room
set room_reserve_state='yes'
where num=#{room_num}
