mapper.select:
select num, member_id, dname, dage, breed, weight, neutral, gender, etc
from A_dogs
where member_id=#{member_id}

SELECT NVL(MAX(ROWNUM), 0)
FROM A_dogs
where member_id=#{member_id}

select id, pwd, email, profile, name, phone, to_char(regdate, 'yyyy.mm.dd hh24"시"mm"분"')as regdate
from A_member
where id=#{id}

select id, regdate
from A_member


		where name=#{name} and email=#{email}


		where name=#{name} and phone=#{phone}

select pwd
from A_member
where id=#{id} and name=#{name} and phone=#{phone}

SELECT id
FROM A_member
WHERE id=#{inputId}

mapper.update:
update A_member
set pwd=#{newPwd}
where id=#{id}

mapper.select:
SELECT COUNT(*)
FROM A_member
WHERE id=#{id} and pwd=#{pwd}

mapper.update:
UPDATE A_member
SET email=#{email}, profile=#{profile}, name=#{name}, phone=#{phone}
WHERE id=#{id}
